import axios from "axios";

export interface MovableGuaranteeWomanTemplate25Params {
  dia: string;
  mes: string;
  año: string;
  nombreCompleto: string;
  edad: string;
  estadoCivil: string;
  dpiLetras: string;
  dia2: string;
  mes2: string;
  año2: string;
  monto: string;
  vehiculoTipo: string;
  vehiculoMarca: string;
  vehiculoColor: string;
  vehiculoUso: string;
  vehiculoChasis: string;
  vehiculoCombustible: string;
  vehiculoMotor: string;
  vehiculoSerie: string;
  vehiculoLinea: string;
  vehiculoModelo: string;
  vehiculoCm3: string;
  vehiculoAsientos: string;
  vehiculoCilindros: string;
  vehiculoIscv: string;
  monto2: string;
  nombreCompleto2: string;
  nombreCompleto3: string;
  plazoLetras: string;
  plazoNumero?: number;
  año2Letras: string;
  monto3: string;
  nombreCompleto4: string;
  nombreCompleto5: string;
  direccion: string;
  correo: string;
  // 🚫 Firmas (Firma, FirmaCashIN) se hacen directo en DocuSeal
}

const DOCUSEAL_API_URL = process.env.DOCUSEAL_API_URL!;
const DOCUSEAL_API_TOKEN = process.env.DOCUSEAL_API_TOKEN!;

const api = axios.create({
  baseURL: DOCUSEAL_API_URL,
  headers: {
    "X-Auth-Token": DOCUSEAL_API_TOKEN,
    "Content-Type": "application/json",
  },
});

export async function generateMovableGuaranteeWomanTemplate25Submission(
  params: MovableGuaranteeWomanTemplate25Params,
  email: string
) {
  try {
    const payload = {
      template_id: 25, // 📌 Template 25: Garantía Mobiliaria Mujer
      submitters: [
        {
          email,
          values: {
            día: params.dia,
            mes: params.mes,
            año: params.año,
            "Nombre Completo": params.nombreCompleto,
            edad: params.edad,
            "estado civil": params.estadoCivil,
            "dpi letras": params.dpiLetras,
            "día ": params.dia2,
            "mes ": params.mes2,
            "año ": params.año2,
            monto: params.monto,
            "Vehículo Tipo": params.vehiculoTipo,
            "Vehículo marca": params.vehiculoMarca,
            "Vehículo color": params.vehiculoColor,
            "Vehículo uso": params.vehiculoUso,
            "Vehículo Chasis": params.vehiculoChasis,
            "Vehículo Combustible": params.vehiculoCombustible,
            "Vehículo Motor": params.vehiculoMotor,
            "Vehículo Serie": params.vehiculoSerie,
            "Vehículo Línea": params.vehiculoLinea,
            "Vehículo Modelo": params.vehiculoModelo,
            "Vehículo cm3": params.vehiculoCm3,
            "Vehículo asientos": params.vehiculoAsientos,
            "Vehículo Cilindros": params.vehiculoCilindros,
            "Vehículo ISCV": params.vehiculoIscv,
            "monto ": params.monto2,
            "Nombre Completo ": params.nombreCompleto2,
            "Nombre Completo 2": params.nombreCompleto3,
            plazo: params.plazoLetras,
            "plazo ": params.plazoNumero,
            "año 2": params.año2Letras,
            "monto 2": params.monto3,
            "Nombre Completo 3": params.nombreCompleto4,
            "Nombre Completo 4": params.nombreCompleto5,
            Dirección: params.direccion,
            correo: params.correo,
            FirmaCashIN:
              "data:image/png;base64,",

            // 🚫 Firmas gestionadas directo en DocuSeal
          },
        },
      ],
    };

    const response = await api.post("/submissions", payload);
    console.log("✅ Submission Garantía Mobiliaria Mujer creado:", response.data);
    return response.data;
  } catch (error: any) {
    console.error(
      "❌ Error al crear submission Garantía Mobiliaria Mujer:",
      error.response?.data || error.message
    );
    throw error;
  }
}
